import Map from '../src/ol/Map.js';
import View from '../src/ol/View.js';
import WKB from '../src/ol/format/WKB.js';
import {OSM, Vector as VectorSource} from '../src/ol/source.js';
import {Tile as TileLayer, Vector as VectorLayer} from '../src/ol/layer.js';

const raster = new TileLayer({
  source: new OSM(),
});

const wkb = [
  // CIRCULARSTRING(1 2, 2 4, 4 2, 5 4, 4 5, 3 6, 4 8) // XY
  '010800000007000000000000000000f03f0000000000000040000000000000004000000000000010400000000000001040000000000000004000000000000014400000000000001040000000000000104000000000000014400000000000000840000000000000184000000000000010400000000000002040',

  // CIRCULARSTRING(11 2, 12 4, 14 2, 15 4, 14 5, 13 6, 14 8, 12 8, 14 8) // XY + full circle
  '',

  // CIRCULARSTRING(21 2, 22 4, 24 2, 25 4, 24 5, 23 6, 24 8, 22 8, 24 8, 26 9, 28 6) // XY + full circle and some
  '',

  // CIRCULARSTRING(31 2 2, 32 4 2, 34 2 2, 35 4 2, 34 5 2, 33 6 2, 34 8 2, 32 8 2, 34 8 2, 36 9 2, 38 6 2) // XYZ  + full circle and some
  '',

  // CIRCULARSTRING(41 2, 42 4, 44 2, 45 4, 44 5, 43 6, 44 8, 48 4, 41 2) // XY closed
  '',
];

const format = new WKB();

const features = wkb.map((wkbGeometry) => {
  return format.readFeature(wkbGeometry, {
    dataProjection: 'EPSG:4326',
    featureProjection: 'EPSG:3857',
  });
});

const vector = new VectorLayer({
  source: new VectorSource({
    features: features,
  }),
});

const map = new Map({
  layers: [raster, vector],
  target: 'map',
  view: new View({
    center: [0, 0],
    zoom: 4,
  }),
});
